    block : 'header',
    attrs : { style : 'border : 2px solid blue;' },
    content : 'Top element'
},
{
    block : 'clearfix',
    attrs : { style : 'border : 2px dotted yellow;' },
    content : [
    {
        block : 'float',
        attrs : { style : 'float : left; border : 1px solid green;' },
        content : 'Floating item 1'
